docs:
{:reduce docs}

		{:if exportedby}
			{=exportedby}{/:if}{:if namespacedby}
			{=namespacedby}{/:if}
			{=kind}
			{=name}{:if desc}
			{=desc}{/:if}{:if type}
			{=type}{/:if}{:if memberof}
			{=memberof}{/:if}{:if param}
			{:reduce param}
				{=type}{=pname}{=pdesc}{/:reduce}
			{/:if}
		{:if members}
		{:reduce members}
			{=.}{/:reduce}
		{/:if}{:if overloads}
		{:reduce overloads}

				{=signature}
				{=desc}
				{:if param}{:reduce param}
					{=type}{=pname}{=pdesc}{/:reduce}
				{/:if}
			{/:reduce}
		{/:if}
	{/:reduce}
